mapper.select:
SELECT A.*, B.totalCnt FROM ( SELECT DIS_MES_NUM, DIS_MES_DT, CASE WHEN LENGTH(DIS_MES_CON) > 35 THEN SUBSTR(DIS_MES_CON, 1, 35) || '...' ELSE DIS_MES_CON END AS DIS_MES_CON, REGEXP_SUBSTR(DIS_MES_AREA, '[^,]+', 1) AS DIS_MES_AREA, DIS_MES_LEVEL, DIS_MES_TYPE, CASE WHEN DIS_MES_CREATE_DT LIKE '%/%' THEN TO_CHAR(TO_TIMESTAMP(DIS_MES_CREATE_DT, 'YYYY/MM/DD HH24:MI:SS.FF'), 'YYYY-MM-DD') ELSE TO_CHAR(TO_DATE(DIS_MES_CREATE_DT, 'YYYY-MM-DD'), 'YYYY-MM-DD') END AS DIS_MES_CREATE_DT, DIS_MES_UPDATED_DT FROM ( SELECT rownum rnum, t1.* FROM ( SELECT * FROM DISASTER_MESSAGE
ORDER BY DIS_MES_CREATE_DT DESC ) t1 WHERE (#{pageNo} * (#{pageSize} - 1) + #{pageNo}) >= ROWNUM ) tt1 WHERE rnum >= (#{pageSize} * (#{pageNo} - 1) + 1) ) A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM DISASTER_MESSAGE
) B ORDER BY DIS_MES_DT DESC
