mapper.select:
SELECT ERB.* FROM (SELECT EBI3.*, EMI.DUTY_NM, EMI.ER_TEL, EMI.GU_CD FROM (SELECT EBI2.* FROM (SELECT EBI1.* , ROW_NUMBER() OVER (PARTITION BY EBI1.HPID ORDER BY EBI1.HVI_DT DESC) AS RNUM FROM ER_BED_INFO EBI1) EBI2 WHERE EBI2.RNUM = 1) EBI3 INNER JOIN EMERGENCY_MEDICAL_INST_INFO EMI ON EBI3.HPID = EMI.HPID) ERB WHERE 1=1
AND ERB.GU_CD = #{gu_Code}
AND ERB.DUTY_NM LIKE #{duty_Nm}

SELECT HPID, GU_CD, DUTY_NM, DUTY_ADDR, DUTY_TEL, ER_TEL, CRT_BY, CRT_DT, MOD_BY, MOD_DT FROM EMERGENCY_MEDICAL_INST_INFO WHERE 1=1
AND GU_CD = #{gu_Code}
AND DUTY_NM LIKE #{duty_Nm}
ORDER BY DUTY_NM ASC
